mapper.select:
select 'warehouse_input' type, warehouse_input_sheet.id sheet_id, product.name product_name, category.name category_name, warehouse_input_sheet_content.quantity, warehouse_input_sheet_content.purchase_price unit_price, warehouse_input_sheet_content.purchase_price * warehouse_input_sheet_content.quantity total_price, warehouse_input_sheet.create_time, '0' batch_id from warehouse_input_sheet, warehouse_input_sheet_content, product, category where state = '审批完成' and warehouse_input_sheet.id = warehouse_input_sheet_content.wi_id and warehouse_input_sheet_content.pid = product.id and product.category_id = category.id and warehouse_input_sheet.create_time <= #{endTime} and warehouse_input_sheet.create_time >= #{beginTime}
union
select 'warehouse_output' type, warehouse_output_sheet.id sheet_id, product.name product_name, category.name category_name, warehouse_output_sheet_content.quantity, warehouse_output_sheet_content.sale_price unit_price, warehouse_output_sheet_content.sale_price * warehouse_output_sheet_content.quantity total_price, warehouse_output_sheet.create_time, warehouse_output_sheet_content.batch_id from warehouse_output_sheet, warehouse_output_sheet_content, product, category where state = '审批完成' and warehouse_output_sheet.id = warehouse_output_sheet_content.wo_id and warehouse_output_sheet_content.pid = product.id and product.category_id = category.id and warehouse_output_sheet.create_time <= #{endTime} and warehouse_output_sheet.create_time >= #{beginTime};
